import ExerciseScreen5 from './components/ExerciseScreen5.vue';
import ResultScreen from './components/ResultScreen.vue';
import { imageUrls } from './imageLoader';
export default {
  components: {
    ExerciseScreen1,
    ExerciseScreen2,
    ExerciseScreen3,
    ExerciseScreen4,
    ExerciseScreen5,
    ResultScreen
  },
  data() {
    return {
      currentExercise: 1,
      appData: {
        images: imageUrls.map(url => ({
          url,
          firstCategory: null,
          secondCategory: null
        })),
        firstOptions: [{
          label: 'Category 1',
          value: 'category-1'
        }, {
          label: 'Category 2',
          value: 'category-2'
        }, {
          label: 'Category 3',
          value: 'category-3'
        }, {
          label: 'Category 4',
          value: 'category-4'
        }, {
          label: 'Category 5',
          value: 'category-5'
        }
        // Define categories for Exercise 1 here
        ],

        firstSelectedOptions: [],
        // Initialize with empty options
        secondOptions: [{
          label: 'Category A',
          value: 'category-A'
        }, {
          label: 'Category B',
          value: 'category-B'
        }, {
          label: 'Category C',
          value: 'category-C'
        }, {
          label: 'Category D',
          value: 'category-D'
        }, {
          label: 'Category E',
          value: 'category-E'
        }
        // Define categories for Exercise 3 here
        ],

        secondSelectedOptions: [],
        // Initialize with empty options
        firstFavoriteCategory: '',
        secondFavoriteCategory: '',
        firstMotivation: '',
        secondMotivation: ''
      }
    };
  },
  computed: {
    currentExerciseComponent() {
      // Define mapping between exercise numbers and component names
      const componentMap = {
        1: 'ExerciseScreen1',
        2: 'ExerciseScreen2',
        3: 'ExerciseScreen3',
        4: 'ExerciseScreen4',
        5: 'ExerciseScreen5',
        summary: 'ResultScreen'
      };
      return componentMap[this.currentExercise];
    },
    imagesForFirstCategory() {
      return this.appData.images.filter(image => image.firstCategory === this.appData.firstFavoriteCategory);
    },
    imagesForSecondCategory() {
      return this.appData.images.filter(image => image.secondCategory === this.appData.secondFavoriteCategory);
    },
    currentExerciseHeading() {
      switch (this.currentExercise) {
        case 1:
          return 'Exercise 1: Categorize Images';
        case 2:
          return 'Exercise 2: Choose Favorite Categories';
        case 3:
          return 'Exercise 3: Recategorize Images';
        case 4:
          return 'Exercise 4: Recategorize Images';
        case 5:
          return 'Exercise 5: Recategorize Images';
        // ... add headings for other exercises ...
        case 'summary':
          return 'Result Summary';
        default:
          return 'Unknown Exercise';
      }
    }
  },
  methods: {
    handleExerciseComplete() {
      this.currentExercise++;
    }
  }
};
