        4: 'Exercise 4: Recategorize Images',
        5: 'Exercise 5: Recategorize Images',
        // ... add headings for other exercises ...
        summary: 'Result Summary'
      };
      return exerciseHeadings[this.currentExercise] || 'Unknown Exercise';
    }
  },
  methods: {
    handleExerciseComplete(categorizedImages) {
      this.images = categorizedImages;
      // Move to the next exercise
      this.currentExercise++;
    }
  }
