import ExerciseScreen1 from './components/ExerciseScreen1.vue';
import ExerciseScreen2 from './components/ExerciseScreen2.vue';
import ExerciseScreen3 from './components/ExerciseScreen3.vue';
import ExerciseScreen4 from './components/ExerciseScreen4.vue';
import ExerciseScreen5 from './components/ExerciseScreen5.vue';
import ResultScreen from './components/ResultScreen.vue';
import { imageUrls } from './imageLoader';
export default {
  components: {
    ExerciseScreen1,
    ExerciseScreen2,
    ExerciseScreen3,
    ExerciseScreen4,
    ExerciseScreen5,
    ResultScreen
  },
  data() {
    return {
      currentExercise: 1,
      images: imageUrls.map(url => ({
        url,
        exercise1Category: null,
        recategorizeCategories: null
      })),
      exercise1Categories: [{
        label: 'Category 1',
        value: 'category-1'
      }, {
        label: 'Category 2',
        value: 'category-2'
      }
      // Define categories for Exercise 1 here
      ],

      recategorizeCategories: [{
        label: 'Category X',
        value: 'category-x'
      }, {
        label: 'Category Y',
        value: 'category-y'
      }
      // Define categories for Exercise 3 here
      ],

      selectedFavoriteCategory: '' // Track the selected favorite category from Exercise 4
    };
  },

  computed: {
    currentExerciseComponent() {
      // Define mapping between exercise numbers and component names
      const componentMap = {
        1: 'ExerciseScreen1',
        2: 'ExerciseScreen2',
        3: 'ExerciseScreen3',
        4: 'ExerciseScreen4',
        5: 'ExerciseScreen5',
        summary: 'ResultScreen'
      };
      return componentMap[this.currentExercise];
    },
    imagesForSelectedCategory() {
      return this.images.filter(image => image.exercise1Category === this.selectedFavoriteCategory);
    },
    currentExerciseHeading() {
      switch (this.currentExercise) {
        case 1:
          return 'Exercise 1: Categorize Images';
        case 2:
          return 'Exercise 2: Choose Favorite Category';
        case 3:
          return 'Exercise 3: Recategorize Images';
        // ... add headings for other exercises ...
        case 'summary':
          return 'Result Summary';
        default:
          return 'Unknown Exercise';
      }
    }
  },
  methods: {
    handleExerciseComplete(categorizedImages) {
      this.images = categorizedImages;
      // Move to the next exercise
      this.currentExercise++;
    }
  }
};
